import json
from web3 import Web3
import requests

infura_url="https://mainnet.infura.io/v3/2bcd1b8ee19044b18a87d5ac9f99c40a"
web3 = Web3(Web3.HTTPProvider(infura_url))

print(web3.isConnected()) 

def fetchABI(addr):
    ABI_ENDPOINT = 'https://api.etherscan.io/api?module=contract&action=getabi&address='+ addr
    response = requests.get(ABI_ENDPOINT)
    response_json = response.json()
    abi_json = json.loads(response_json['result'])
    return abi_json

address=Web3.toChecksumAddress("0x9D0Ce949F9E06D62a1e35789666ed43c5E1C58D0")
